INSERT INTO application_form_section (id, application_form_id, section_number, title, fields, section_type) VALUES (17, 1, 17, 'Assessment Criteria - Performance Metrics', '[{"name": "metrics", "type": "simpleform", "isEnabled": true, "isNumeric": false, "simpleform": [{"name": "metrics.performanceimprovement", "type": "textarea", "label": "How will this innovation provide better performing low-carbon heating systems than those currently on the market (max 500 words)?", "helptext": "You should consider:\n\n-  How performance metrics will be calculated\n\n-  Current and anticipated performance parameters e.g. SPFH4\n\n-  Energy Demand\n\n-  Peak load factors\n\n-  Consumer experience\n\nPlease provide explanation and supporting evidence.", "maxWords": 500, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}, {"name": "metrics.performancemonitoring", "type": "textarea", "label": "Please describe how the performance of your product will be monitored, in situ, throughout the lifetime of the product (max 250 words) ", "maxWords": 250, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}, {"name": "metrics.performanceintegration", "type": "textarea", "label": "How does the innovation integrate into the overall heating system (max 250 words)?", "helptext": "Please address the following:\n\n-  The type or types of heating system the innovation you are proposing might apply to and the types of buildings this system could be installed in \n\n-  Integration with/of other low-carbon heating technologies\n\n-  Integration with the wider energy system ", "maxWords": 250, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}]}]', 'simpleform');
INSERT INTO application_form_section (id, application_form_id, section_number, title, fields, section_type) VALUES (18, 1, 18, 'Assessment Criteria - Market potential', '[{"name": "pot", "type": "simpleform", "isEnabled": true, "isNumeric": false, "simpleform": [{"name": "pot.marketpotential", "type": "textarea", "label": "What is the market potential for your innovation both in the UK and internationally (max 250 words)?", "helptext": "Please describe:\n\n-  The size and nature of the market opportunities\n\n-  The proposed exploitation route and timescales to market  \n\n-  How value is expected to be generated from the innovation\n\n-  Any further technology development needed to secure sales", "maxWords": 250, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}]}]', 'simpleform');
INSERT INTO application_form_section (id, application_form_id, section_number, title, fields, section_type) VALUES (19, 1, 19, 'Assessment Criteria - Project Delivery', '[{"name": "projectdelivery", "type": "simpleform", "isEnabled": true, "isNumeric": false, "simpleform": [{"name": "projectdelivery.skills", "type": "textarea", "label": "Please provide details below of the relevant skills, qualifications and experience of the project team (max 1000 words).", "helptext": "Please include descriptions and evidence of previous relevant work carried out, including the date, location, client and project size.", "maxWords": 1000, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}, {"name": "projectdelivery.consortium", "type": "textarea", "label": "If the project is being delivered as part of consortium please outline below the key roles for each partner and the proposed governance arrangements to ensure effective project delivery (max 500 words).", "maxWords": 500, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": false}, {"name": "projectdelivery.listexternalparty", "type": "textarea", "label": "If any external party is responsible for delivering goods or services worth more than 10% of the total project value please explain how you will ensure delays will be prevented (max 250 words).", "maxWords": 500, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": false}]}]', 'simpleform');
