INSERT INTO application_form_section (id, application_form_id, section_number, title, fields, section_type) VALUES (24, 1, 24, 'Assessment Criteria - Wider Objectives', '[{"name": "wider", "type": "simpleform", "isEnabled": true, "isNumeric": false, "simpleform": [{"name": "wider.marketpotential", "type": "textarea", "label": "How does the innovation contribute to the wider objectives of this Low-Carbon Heating Technology Innovation Fund (max 250 words)?", "helptext": "You may wish to address the following:\n\n-  Fuel poverty\n\n-  Consumer acceptance and the improving the attractiveness of low-carbon heating technologies\n\n-  Supply chain strengthening\n\n-  Peaking (particularly impacts on peak electricity demand)\n\n-  Demand management \n\n-  Off-gas grid decarbonisation \n\n-  Avoidance of unintended consequences associated with the retrofit of low-carbon heating systems into existing buildings\n\n-  Please provide explanation and supporting evidence for the benefits claimed.", "maxWords": 250, "fieldType": "textarea", "isEnabled": true, "isNumeric": false, "isMandatory": true}]}]', 'simpleform');
